<?phpif(!isset($_REQUEST['a'])) $action = 'null'; else $action = $_REQUEST['a'];switch($action) {    case 'disconnect':        // deconnexion, suppression de la session        // rediriger sur la page de login        break;    case 'postLogin': // URL ou le formulaire de connexion sera post        // traitement pour la connexion        // mettre en session        // rediriger sur le panel admin        break;    case 'changePass':        include './vues/login/v_changePass.php';        break;    case 'postChangePass':        // traitement changePass        // mettre a jour en session        // rediriger sur le panel admin        break;	default: // action login        include './vues/login/v_connexion.php';        break;}