<?php// fonction de connexionfunction connexion($login,$pass){    global $db;    //netoyage des valeurs pour eviter les failles SQL !    $login = $db->quote($login);    $pass = $db->quote($pass);    $pass = md5($pass);    $q_log="SELECT count(*) as nb , `id` FROM `login` WHERE `login`=".$login." AND `pass`='".$pass."' ;";    $q_res = $db->query($q_log);    $a_log = $q_res->fetch(PDO::FETCH_ASSOC);    return $a_log;}